package actividadesBucles.ActsFor;

public class MainMultiplos7
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub

		int multiplo = 7;

		for (int i = 0; i < 100; i++)
		{
			int resultado = multiplo * i;

			if (resultado < 100)
			{
				System.out.println(resultado);
			}
		}

	}

}
